#pragma once

#include <iostream>
#include <portaudio.h>
#include <filesystem>
#include <vector>
#include <mutex>
#include <thread>

#include "AudioData.h"
#include "SoundFileIO.h"

namespace iamaprogrammer {
  class AudioStream {
    public:
      enum State {
        PLAYING,
        STOPPED,
        BUFFERING
      };

      AudioStream();
      AudioStream(std::filesystem::path filePath);

      void setFile(std::filesystem::path filePath);

      void setup();

      void start();
      void stop();

      void end();

      std::filesystem::path getFilePath();
      State getState();

    private:
      struct AudioBuffer {
        iamaprogrammer::AudioData* data;
        long start = 0;
      };
      
      // Essential
      std::filesystem::path filePath;
      iamaprogrammer::AudioReader reader;
      std::thread audioReaderThread;

      AudioBuffer audioBuffer;
      PaStream* stream;

      // State
      State state = State::STOPPED;
      PaError error;

      void handlePaError();
      void audioReaderThreadCallback();

      static int paCallback(
        const void *inputBuffer, void *outputBuffer,
        unsigned long framesPerBuffer,
        const PaStreamCallbackTimeInfo* timeInfo,
        PaStreamCallbackFlags statusFlags,
        void *userData 
      ) {
        float* out = static_cast<float*>(outputBuffer);
        AudioBuffer* audioBuffer = static_cast<AudioBuffer*>(userData);

        for (unsigned long i = 0; i < framesPerBuffer; i++) {
          *out++ = audioBuffer->data->buffer.at(audioBuffer->start); // Copy from buffer to output
          *out++ = audioBuffer->data->buffer.at(audioBuffer->start + 1);
          audioBuffer->start+=2;
        }
        //std::cout << "Stream started" << std::endl;

        //std::cout << toString((float*)outputBuffer, framesPerBuffer) << "\n\n" << std::endl;
        //audioBuffer->start += framesPerBuffer;

        return paContinue;
      }
  };
}


